import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import PatientDashboard from './components/PatientDashboard';
import DoctorDashboard from './components/DoctorDashboard';
import StaffDashboard from './components/StaffDashboard';
import PromotionSection from './components/PromotionSection';
import MapSection from './components/MapSection';
import ReviewSection from './components/ReviewSection';

function App() {
  const [user, setUser] = useState(null);

  const handleLogin = (userData) => {
    console.log('handleLogin called with:', userData); // Debug
    setUser(userData);
  };
  const handleLogout = () => {
    console.log('handleLogout called'); // Debug
    setUser(null);
  };

  console.log('Current user in App:', user); // Debug

  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-blue-50">
        <Header user={user} onLogout={handleLogout} />
        <main className="flex-grow container mx-auto p-4">
          <Routes>
            {/* หน้าแรก รวม Promotion, Map, Review */}
            <Route
              path="/"
              element={
                <>
                  <PromotionSection />
                  <MapSection />
                  <ReviewSection />
                </>
              }
            />

            {/* Auth */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register />} />

            {/* Dashboards */}
            <Route
              path="/patient-dashboard"
              element={
                user ? (
                  <PatientDashboard user={user} />
                ) : (
                  <div>
                    <p className="text-red-500">Access denied. Please log in.</p>
                    <Login onLogin={handleLogin} />
                  </div>
                )
              }
            />
            <Route
              path="/doctor-dashboard"
              element={
                user && user.role === 'doctor' ? (
                  <DoctorDashboard user={user} />
                ) : (
                  <Login onLogin={handleLogin} />
                )
              }
            />
            <Route
              path="/staff-dashboard"
              element={
                user && user.role === 'staff' ? (
                  <StaffDashboard user={user} />
                ) : (
                  <Login onLogin={handleLogin} />
                )
              }
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
