import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Login from "./components/Login";
import Register from "./components/Register";
import PatientDashboard from "./components/PatientDashboard";
import DoctorDashboard from "./components/DoctorDashboard";
import StaffDashboard from "./components/StaffDashboard";
import PromotionSection from "./components/PromotionSection";
import MapSection from "./components/MapSection";
import ReviewSection from "./components/ReviewSection";

function App() {
  const [user, setUser] = useState(() => {
    const saved = localStorage.getItem("user");
    return saved ? JSON.parse(saved) : null;
  });

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-blue-50">
        <Header user={user} onLogout={handleLogout} />
        <main className="flex-grow container mx-auto p-4">
          <Routes>
            {/* หน้าแรก */}
            <Route
              path="/"
              element={
                <div className="space-y-8">
                  <section>
                    <h2 className="text-2xl font-bold mb-4 text-blue-600">Promotion</h2>
                    <PromotionSection />
                  </section>
                  <section>
                    <h2 className="text-2xl font-bold mb-4 text-blue-600">Map</h2>
                    <MapSection />
                  </section>
                  <section>
                    <h2 className="text-2xl font-bold mb-4 text-blue-600">Reviews</h2>
                    <ReviewSection />
                  </section>
                </div>
              }
            />

            {/* Auth */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register />} />

            {/* Dashboards */}
            <Route
              path="/patient-dashboard"
              element={user?.role === "patient" ? <PatientDashboard user={user} /> : <Navigate to="/login" replace />}
            />
            <Route
              path="/doctor-dashboard"
              element={user?.role === "doctor" ? <DoctorDashboard user={user} /> : <Navigate to="/login" replace />}
            />
            <Route
              path="/staff-dashboard"
              element={user?.role === "staff" ? <StaffDashboard user={user} /> : <Navigate to="/login" replace />}
            />

            {/* fallback */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
