// PatientDashboard.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function PatientDashboard({ user }) {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState("");
  const [newAppointmentDate, setNewAppointmentDate] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!user?.username) return;

    const fetchPatientData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(
            user.username
          )}`
        );
        const data = await response.json();
        if (response.ok) setPatientData(data);
        else setError(data.error || "Failed to fetch patient data");
      } catch (err) {
        console.error(err);
        setError("Network error");
      }
    };

    fetchPatientData();
  }, [user]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return "N/A";
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    return `${years} ปี`;
  };

  const canConfirm = () => {
    if (!patientData?.appointmentDate) return false;
    const appointment = new Date(patientData.appointmentDate);
    const today = new Date();
    const oneDayBefore = new Date(appointment);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appointment;
  };

  const confirmAppointment = () => {
    alert("ยืนยันนัดหมายสำเร็จ!");
  };

  const rescheduleAppointment = () => {
    if (!newAppointmentDate) {
      alert("กรุณาเลือกวันนัดหมายใหม่");
      return;
    }
    alert(`เลื่อนนัดหมายไปวันที่ ${new Date(newAppointmentDate).toLocaleDateString()}`);
  };

  const cancelAppointment = () => {
    alert("ยกเลิกนัดหมายเรียบร้อย");
  };

  return (
    <div className="min-h-screen bg-blue-50 flex items-center justify-center p-6">
      <div className="w-full max-w-3xl bg-white shadow-lg rounded-xl p-6 space-y-6">
        <h1 className="text-3xl font-bold text-blue-600 text-center">
          Patient Dashboard
        </h1>

        {error && <div className="text-red-500 text-center">{error}</div>}

        {patientData ? (
          <>
            {/* Patient Info */}
            <div className="flex justify-between items-center bg-blue-100 p-4 rounded-lg">
              <div>
                <p className="font-semibold text-gray-700">
                  ชื่อ: <span className="font-normal">{patientData.fullName}</span>
                </p>
                <p className="font-semibold text-gray-700">
                  ID: <span className="font-normal">{patientData.userId}</span>
                </p>
              </div>
              {patientData.appointmentDate && (
                <div className="text-right text-gray-700">
                  <p className="font-semibold">วันนัดหมาย:</p>
                  <p>{new Date(patientData.appointmentDate).toLocaleDateString()}</p>
                </div>
              )}
            </div>

            {/* Appointment Actions */}
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 bg-gray-50 p-4 rounded-lg shadow-inner">
              <button
                onClick={confirmAppointment}
                disabled={!canConfirm()}
                className={`w-full md:w-auto bg-green-500 text-white px-6 py-2 rounded hover:bg-green-600 transition ${
                  !canConfirm() ? "opacity-50 cursor-not-allowed" : ""
                }`}
              >
                ยืนยันนัดหมาย
              </button>

              <div className="flex gap-2 items-center w-full md:w-auto">
                <input
                  type="date"
                  value={newAppointmentDate}
                  onChange={(e) => setNewAppointmentDate(e.target.value)}
                  className="border p-2 rounded flex-grow"
                />
                <button
                  onClick={rescheduleAppointment}
                  className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600 transition"
                >
                  เลื่อนนัดหมาย
                </button>
              </div>

              <button
                onClick={cancelAppointment}
                className="w-full md:w-auto bg-red-500 text-white px-6 py-2 rounded hover:bg-red-600 transition"
              >
                ยกเลิกนัด
              </button>
            </div>
          </>
        ) : (
          <p className="text-center text-gray-500">Loading...</p>
        )}
      </div>
    </div>
  );
}

export default PatientDashboard;
