import React from 'react';
import { Navigate } from 'react-router-dom';

function ProtectedRoute({ user, role, children }) {
  // Debug log เพื่อตรวจสอบสถานะของ user และ role
  console.log('ProtectedRoute checked, user:', user, 'required role:', role);

  // ตรวจสอบเงื่อนไขการเข้าถึง
  if (!user) {
    console.log('Access denied, no user logged in');
    return <Navigate to="/login" replace />;
  }

  if (!user.role) {
    console.log('Access denied, user has no role');
    return <Navigate to="/login" replace />;
  }

  if (user.role !== role) {
    console.log('Access denied, role mismatch, got:', user.role, 'expected:', role);
    return <Navigate to="/login" replace />;
  }

  // ถ้าผ่านเงื่อนไขทั้งหมด ให้แสดง children (เช่น PatientDashboard)
  console.log('Access granted, rendering children');
  return children;
}

export default ProtectedRoute;
