import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Login from './components/Login';
import PatientDashboard from './components/PatientDashboard';
import PromotionSection from './components/PromotionSection';
import MapSection from './components/MapSection';
import ReviewSection from './components/ReviewSection';

function App() {
  const [user, setUser] = useState(null);

  const handleLogin = (userData) => {
    console.log('User logged in:', userData);
    setUser(userData);
  };

  const handleLogout = () => {
    console.log('User logged out');
    setUser(null);
  };

  console.log('Current user:', user);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {/* Header with Navigation */}
        <header className="p-4 bg-blue-600 text-white flex justify-between items-center">
          <div>
            <Link to="/" className="mr-4 text-white hover:underline">Home</Link>
            {user && <Link to="/patient-dashboard" className="text-white hover:underline">Dashboard</Link>}
          </div>
          <div>
            {user ? (
              <button onClick={handleLogout} className="bg-red-500 p-2 rounded">Logout</button>
            ) : (
              <Link to="/login" className="bg-green-500 p-2 rounded">Login</Link>
            )}
          </div>
        </header>
        <main className="p-4">
          <Routes>
            {/* หน้าหลัก */}
            <Route
              path="/"
              element={
                <>
                  <PromotionSection />
                  <MapSection />
                  <ReviewSection />
                </>
              }
            />
            {/* Login Page */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            {/* Patient Dashboard */}
            <Route
              path="/patient-dashboard"
              element={user && user.role === 'patient' ? <PatientDashboard user={user} /> : <Login onLogin={handleLogin} />}
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
