// App.jsx
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Components
import Header from "./components/Header";
import Footer from "./components/Footer";

// Auth Pages
import Login from "./components/Login";
import Register from "./components/Register";

// Dashboards
import PatientDashboard from "./components/PatientDashboard";
import DoctorDashboard from "./components/DoctorDashboard";
import StaffDashboard from "./components/StaffDashboard";

// Homepage Sections
import PromotionSection from "./components/PromotionSection";
import MapSection from "./components/MapSection";
import ReviewSection from "./components/ReviewSection";

function App() {
  const [user, setUser] = useState(null);

  // ฟังก์ชันเมื่อ Login สำเร็จ
  const handleLogin = (userData, navigate) => {
    setUser(userData);
    if (userData.role === "patient") navigate("/patient-dashboard");
    else if (userData.role === "doctor") navigate("/doctor-dashboard");
    else if (userData.role === "staff") navigate("/staff-dashboard");
  };

  // ฟังก์ชัน Logout
  const handleLogout = () => {
    setUser(null);
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-blue-50">
        {/* Header */}
        <Header user={user} onLogout={handleLogout} />

        {/* Main Content */}
        <main className="flex-grow container mx-auto p-4">
          <Routes>
            {/* หน้าแรก */}
            <Route
              path="/"
              element={
                <>
                  <PromotionSection />
                  <MapSection />
                  <ReviewSection />
                </>
              }
            />

            {/* Auth */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register />} />

            {/* Patient Dashboard */}
            <Route
              path="/patient-dashboard"
              element={
                user?.role === "patient" ? (
                  <PatientDashboard user={user} />
                ) : (
                  <Navigate to="/login" replace />
                )
              }
            />

            {/* Doctor Dashboard */}
            <Route
              path="/doctor-dashboard"
              element={
                user?.role === "doctor" ? (
                  <DoctorDashboard user={user} />
                ) : (
                  <Navigate to="/login" replace />
                )
              }
            />

            {/* Staff Dashboard */}
            <Route
              path="/staff-dashboard"
              element={
                user?.role === "staff" ? (
                  <StaffDashboard user={user} />
                ) : (
                  <Navigate to="/login" replace />
                )
              }
            />

            {/* ถ้า path ไม่ตรงอะไรเลย */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>

        {/* Footer */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
