import React, { useEffect, useState } from 'react';

function PatientDashboard({ user }) {
  const [dashboardData, setDashboardData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/patients/dashboard?username=${user.username}`);
        const data = await response.json();
        console.log('Dashboard response:', data);

        if (response.ok && data.data) {
          setDashboardData(data.data);
        } else {
          setError(data.error || 'Failed to load dashboard');
        }
      } catch (err) {
        setError('Network error.');
      }
    };

    if (user?.username) {
      fetchDashboard();
    }
  }, [user]);

  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!dashboardData) return <p>Loading...</p>;

  return (
    <div>
      <h2>Welcome, {dashboardData.fullName}</h2>
      <p>UserID: {dashboardData.userId}</p>
      <p>Role: {dashboardData.role}</p>
      <p>Appointment: {dashboardData.appointmentDate || 'No appointment'}</p>
    </div>
  );
}

export default PatientDashboard;
