// PatientDashboard.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function PatientDashboard({ user }) {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState("");
  const [newDate, setNewDate] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(user.username)}`,
          { method: "GET", headers: { "Content-Type": "application/json" } }
        );
        const data = await response.json();
        if (response.ok) setPatientData(data);
        else setError(data.error || "Failed to fetch patient data");
      } catch (err) {
        console.error("Network error:", err);
        setError("Network error");
      }
    };
    fetchPatientData();
  }, [user.username]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return "N/A";
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    return `${years} ปี`;
  };

  const confirmAppointment = async () => {
    alert("ยืนยันนัดหมายสำเร็จ!");
    // สามารถเพิ่ม API call เพื่อยืนยันนัดได้
  };

  const rescheduleAppointment = async () => {
    if (!newDate) {
      alert("กรุณาเลือกวันนัดใหม่");
      return;
    }
    alert(`เลื่อนนัดหมายไปวันที่ ${new Date(newDate).toLocaleDateString()}`);
    // สามารถเพิ่ม API call เพื่ออัปเดตวันนัดได้
  };

  const cancelAppointment = async () => {
    const confirmCancel = window.confirm("ยืนยันการยกเลิกนัดหมาย?");
    if (confirmCancel) {
      alert("ยกเลิกนัดหมายเรียบร้อยแล้ว");
      // สามารถเพิ่ม API call เพื่อลบ/ยกเลิกนัดได้
      setPatientData({ ...patientData, appointmentDate: null });
    }
  };

  const isConfirmEnabled = () => {
    if (!patientData?.appointmentDate) return false;
    const appointment = new Date(patientData.appointmentDate);
    const today = new Date();
    const oneDayBefore = new Date(appointment);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appointment;
  };

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen rounded-lg shadow-md">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}

      {patientData ? (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Patient Info */}
          <div className="bg-gray-100 p-4 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">ข้อมูลคนไข้</h2>
            <p><strong>ชื่อ:</strong> {patientData.fullName}</p>
            <p><strong>ID:</strong> {patientData.userId}</p>
            <p><strong>อายุ:</strong> {calculateAge(patientData.birthDate)}</p>
          </div>

          {/* Appointment */}
          {patientData.appointmentDate && (
            <div className="bg-gray-100 p-4 rounded-lg shadow">
              <h2 className="text-xl font-semibold text-gray-700 mb-2">นัดหมาย</h2>
              <p><strong>วันที่นัด:</strong> {new Date(patientData.appointmentDate).toLocaleDateString()}</p>
              <p><strong>สถานะ:</strong> {patientData.status || "Pending"}</p>

              {/* Buttons */}
              <div className="mt-4 space-x-2">
                <button
                  onClick={confirmAppointment}
                  disabled={!isConfirmEnabled()}
                  className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 ${!isConfirmEnabled() ? "opacity-50 cursor-not-allowed" : ""}`}
                >
                  ยืนยันนัดหมาย
                </button>

                <input
                  type="date"
                  value={newDate}
                  onChange={(e) => setNewDate(e.target.value)}
                  className="border p-2 rounded mr-2"
                />
                <button
                  onClick={rescheduleAppointment}
                  className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
                >
                  เลื่อนนัดหมาย
                </button>

                <button
                  onClick={cancelAppointment}
                  className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                >
                  ยกเลิกนัด
                </button>
              </div>
            </div>
          )}
        </div>
      ) : (
        <p className="text-center">Loading...</p>
      )}
    </div>
  );
}

export default PatientDashboard;
