// PatientDashboard.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function PatientDashboard({ user }) {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState("");
  const [newAppointmentDate, setNewAppointmentDate] = useState("");
  const navigate = useNavigate();

  // Fetch ข้อมูลคนไข้
  useEffect(() => {
    if (!user?.username) return;

    const fetchPatientData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(
            user.username
          )}`
        );
        const data = await response.json();
        if (response.ok) setPatientData(data);
        else setError(data.error || "Failed to fetch patient data");
      } catch (err) {
        console.error(err);
        setError("Network error");
      }
    };

    fetchPatientData();
  }, [user]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return "N/A";
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    return `${years} ปี`;
  };

  // ตรวจสอบปุ่มยืนยันนัดหมาย (กดได้ล่วงหน้า 1 วัน)
  const canConfirm = () => {
    if (!patientData?.appointmentDate) return false;
    const appointment = new Date(patientData.appointmentDate);
    const today = new Date();
    const oneDayBefore = new Date(appointment);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appointment;
  };

  const confirmAppointment = () => {
    alert("ยืนยันนัดหมายสำเร็จ!");
  };

  const rescheduleAppointment = () => {
    if (!newAppointmentDate) {
      alert("กรุณาเลือกวันนัดหมายใหม่");
      return;
    }
    alert(`เลื่อนนัดหมายไปวันที่ ${new Date(newAppointmentDate).toLocaleDateString()}`);
    // TODO: เรียก API เพื่ออัปเดตวันนัดหมาย
  };

  const cancelAppointment = () => {
    alert("ยกเลิกนัดหมายเรียบร้อย");
    // TODO: เรียก API เพื่อยกเลิกนัด
  };

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      {patientData && (
        <div className="bg-gray-100 p-4 rounded-lg shadow space-y-4">
          {/* ข้อมูลคนไข้มุมบนซ้าย */}
          <div>
            <p><strong>ชื่อ:</strong> {patientData.fullName}</p>
            <p><strong>ID:</strong> {patientData.userId}</p>
          </div>

          {/* วันนัดหมาย */}
          {patientData.appointmentDate && (
            <div>
              <p>
                <strong>วันนัดหมาย:</strong>{" "}
                {new Date(patientData.appointmentDate).toLocaleDateString()}
              </p>
            </div>
          )}

          {/* ปุ่มจัดการนัด */}
          <div className="flex flex-wrap gap-2 mt-4">
            <button
              onClick={confirmAppointment}
              disabled={!canConfirm()}
              className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 ${
                !canConfirm() ? "opacity-50 cursor-not-allowed" : ""
              }`}
            >
              ยืนยันนัดหมาย
            </button>

            <div className="flex gap-2 items-center">
              <input
                type="date"
                value={newAppointmentDate}
                onChange={(e) => setNewAppointmentDate(e.target.value)}
                className="border p-1 rounded"
              />
              <button
                onClick={rescheduleAppointment}
                className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
              >
                เลื่อนนัดหมาย
              </button>
            </div>

            <button
              onClick={cancelAppointment}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              ยกเลิกนัด
            </button>
          </div>
        </div>
      )}
      {!patientData && <p>Loading...</p>}
    </div>
  );
}

export default PatientDashboard;
