import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import PatientDashboard from './components/PatientDashboard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);

  // โหลด user จาก localStorage เมื่อ App เริ่มทำงาน
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (err) {
        console.error('Failed to parse user from localStorage:', err);
        localStorage.removeItem('user');
        setUser(null);
      }
    }
    setLoadingUser(false);
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  if (loadingUser) {
    return <p>Loading...</p>;
  }

  return (
    <Router>
      <div className="container mx-auto p-4">
        <Routes>
          {/* Home page */}
          <Route
            path="/"
            element={
              <div className="max-w-4xl mx-auto mt-10 p-6 bg-white rounded shadow">
                <h1 className="text-3xl font-bold mb-4 text-center">Welcome to Dental Clinic</h1>

                <div className="flex justify-center gap-4 mb-6">
                  {!user && (
                    <>
                      <Link
                        to="/login"
                        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                      >
                        Login
                      </Link>
                      <Link
                        to="/register"
                        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                      >
                        Sign Up
                      </Link>
                    </>
                  )}
                  {user && (
                    <button
                      onClick={handleLogout}
                      className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                    >
                      Logout
                    </button>
                  )}
                </div>

                {/* รีวิว */}
                <div className="mb-6">
                  <h2 className="text-2xl font-bold mb-2">Patient Reviews</h2>
                  <ul className="list-disc list-inside">
                    <li>“Excellent service and friendly staff!”</li>
                    <li>“Very professional dentists, highly recommend.”</li>
                    <li>“Clean clinic and great experience.”</li>
                  </ul>
                </div>

                {/* แผนที่ */}
                <div className="mb-6">
                  <h2 className="text-2xl font-bold mb-2">Clinic Location</h2>
                  <div className="w-full h-64">
                    <iframe
                      title="clinic-map"
                      src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3875.1298456035655!2d100.516111!3d13.756331!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x30e29ed2ff3a3e59%3A0x23d7c4a2c6b8!2sBangkok%2C%20Thailand!5e0!3m2!1sen!2sus!4v1693555200000!5m2!1sen!2sus"
                      width="100%"
                      height="100%"
                      style={{ border: 0 }}
                      allowFullScreen=""
                      loading="lazy"
                      referrerPolicy="no-referrer-when-downgrade"
                    ></iframe>
                  </div>
                </div>
              </div>
            }
          />

          {/* Login */}
          <Route
            path="/login"
            element={user ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />}
          />

          {/* Register */}
          <Route
            path="/register"
            element={user ? <Navigate to="/dashboard" /> : <Register />}
          />

          {/* Dashboard */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute user={user} role="patient">
                <PatientDashboard user={user} onLogout={handleLogout} />
              </ProtectedRoute>
            }
          />

          {/* Redirect หน้าอื่นไป Home */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
