import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function PatientDashboard() {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const username = new URLSearchParams(location.search).get('username') || 'testuser'; // รับจาก query หรือค่าเริ่มต้น

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(username)}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        const data = await response.json();
        console.log('Dashboard data:', data); // Debug: ดูข้อมูลที่ได้จาก API
        if (response.ok) {
          setPatientData(data);
        } else {
          setError(data.error || 'Failed to fetch patient data');
        }
      } catch (err) {
        console.error('Network error:', err);
        setError('Network error or server unavailable');
      }
    };
    fetchPatientData();
  }, [username]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return 'N/A';
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    let months = today.getMonth() - birth.getMonth();
    let days = today.getDate() - birth.getDate();

    if (months < 0 || (months === 0 && days < 0)) {
      years--;
      months += 12;
    }
    if (days < 0) {
      months--;
      const lastMonth = new Date(today.getFullYear(), today.getMonth() - 1, 0);
      days += lastMonth.getDate();
    }
    return `${years} ปี ${months} เดือน`;
  };

  const viewReceipt = (patientId) => {
    navigate(`/receipt/${patientId}`);
  };

  return (
    <div className="container mx-auto p-4 bg-white text-blue-800">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      {patientData ? (
        <table className="w-full border-collapse border border-blue-300">
          <thead>
            <tr className="bg-blue-100">
              <th className="border border-blue-300 p-2">Patient ID</th>
              <th className="border border-blue-300 p-2">Full Name</th>
              <th className="border border-blue-300 p-2">Age</th>
              <th className="border border-blue-300 p-2">Chronic Disease</th>
              <th className="border border-blue-300 p-2">Drug Allergy</th>
              <th className="border border-blue-300 p-2">Food Allergy</th>
              <th className="border border-blue-300 p-2">Appointment Date</th>
              <th className="border border-blue-300 p-2">Status</th>
              <th className="border border-blue-300 p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr className="hover:bg-blue-50">
              <td className="border border-blue-300 p-2">{patientData.patientId || 'N/A'}</td>
              <td className="border border-blue-300 p-2">{patientData.fullName || 'Not provided'}</td>
              <td className="border border-blue-300 p-2">{calculateAge(patientData.birthDate) || 'N/A'}</td>
              <td className="border border-blue-300 p-2">{patientData.chronicDisease || 'None'}</td>
              <td className="border border-blue-300 p-2">{patientData.drugAllergy || 'None'}</td>
              <td className="border border-blue-300 p-2">{patientData.foodAllergy || 'None'}</td>
              <td className="border border-blue-300 p-2">{patientData.appointmentDate ? new Date(patientData.appointmentDate).toLocaleDateString() : 'N/A'}</td>
              <td className="border border-blue-300 p-2">{patientData.status || 'Pending'}</td>
              <td className="border border-blue-300 p-2">
                <button
                  onClick={() => viewReceipt(patientData.patientId)}
                  className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
                  disabled={!patientData.patientId}
                >
                  View Receipt
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      ) : (
        <p className="text-center">Loading...</p>
      )}
    </div>
  );
}

export default PatientDashboard;
