import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import PatientDashboard from './components/PatientDashboard';
import PromotionSection from './components/PromotionSection';
import MapSection from './components/MapSection';
import ReviewSection from './components/ReviewSection';
import Register from './components/Register';

function App() {
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true); // ✅ เพิ่ม loading state
  const navigate = useNavigate();

  console.log('App rendered, current user:', user);

  // โหลด user จาก localStorage ตอน mount
  useEffect(() => {
    const savedUser = localStorage.getItem('user');

    if (savedUser && savedUser !== 'undefined' && savedUser !== 'null') {
      try {
        setUser(JSON.parse(savedUser));
      } catch (error) {
        console.error('Failed to parse saved user from localStorage:', error);
        localStorage.removeItem('user');
      }
    }

    setLoadingUser(false); // โหลดเสร็จ
  }, []);

  const handleLogin = (userData) => {
    console.log('Handling login with userData:', userData);
    if (userData) {
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      navigate('/patient-dashboard'); // ✅ redirect หลัง setUser
    }
  };

  const handleLogout = () => {
    console.log('User logged out');
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="p-4 bg-blue-600 text-white flex justify-between items-center">
        <div>
          <Link to="/" className="mr-4 text-white hover:underline">Home</Link>
          {user && <Link to="/patient-dashboard" className="text-white hover:underline">Dashboard</Link>}
        </div>
        <div>
          {user ? (
            <button onClick={handleLogout} className="bg-red-500 p-2 rounded">Logout</button>
          ) : (
            <>
              <Link to="/login" className="bg-green-500 p-2 rounded mr-2">Login</Link>
              <Link to="/register" className="bg-blue-500 p-2 rounded">Sign Up</Link>
            </>
          )}
        </div>
      </header>
      <main className="p-4">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <PromotionSection />
                <MapSection />
                <ReviewSection />
              </>
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/patient-dashboard"
            element={
              loadingUser
                ? <p>Loading...</p> // ✅ แสดง loading จนกว่าจะโหลด user
                : user && user.role === 'patient'
                  ? <PatientDashboard user={user} />
                  : <Navigate to="/login" />
            }
          />
        </Routes>
      </main>
    </div>
  );
}

export default App;
