import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import PatientDashboard from './components/PatientDashboard';
import PromotionSection from './components/PromotionSection';
import MapSection from './components/MapSection';
import ReviewSection from './components/ReviewSection';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);
  const navigate = useNavigate();

  // 1️⃣ โหลด user จาก localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    console.log('Loading user from localStorage:', savedUser);

    if (savedUser && savedUser !== 'undefined' && savedUser !== 'null') {
      try {
        const parsedUser = JSON.parse(savedUser);
        console.log('Parsed user:', parsedUser);
        setUser(parsedUser);
      } catch (error) {
        console.error('Failed to parse user from localStorage:', error);
        localStorage.removeItem('user');
      }
    } else {
      console.log('No valid user found in localStorage');
    }

    setLoadingUser(false);
  }, []);

  // 2️⃣ ฟังก์ชัน login
  const handleLogin = (userData) => {
    if (userData && userData.role) {
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      navigate('/patient-dashboard', { replace: true });
    } else {
      console.warn('Invalid userData:', userData);
    }
  };

  // 3️⃣ ฟังก์ชัน logout
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login', { replace: true });
  };

  // 4️⃣ Redirect อัตโนมัติหลังโหลด user
  useEffect(() => {
    if (!loadingUser) {
      if (user && user.role === 'patient' && window.location.pathname === '/login') {
        navigate('/patient-dashboard', { replace: true });
      } else if (!user && window.location.pathname === '/patient-dashboard') {
        navigate('/login', { replace: true });
      }
    }
  }, [user, loadingUser, navigate]);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="p-4 bg-blue-600 text-white flex justify-between items-center">
        <div>
          <Link to="/" className="mr-4 text-white hover:underline">Home</Link>
          {user && <Link to="/patient-dashboard" className="text-white hover:underline">Dashboard</Link>}
        </div>
        <div>
          {user ? (
            <button onClick={handleLogout} className="bg-red-500 p-2 rounded">Logout</button>
          ) : (
            <>
              <Link to="/login" className="bg-green-500 p-2 rounded mr-2">Login</Link>
              <Link to="/register" className="bg-blue-500 p-2 rounded">Sign Up</Link>
            </>
          )}
        </div>
      </header>

      {/* Main Content */}
      <main className="p-4">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <PromotionSection />
                <MapSection />
                <ReviewSection />
              </>
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/patient-dashboard"
            element={
              <ProtectedRoute user={user} role="patient" loadingUser={loadingUser}>
                <PatientDashboard user={user} />
              </ProtectedRoute>
            }
          />
        </Routes>
      </main>
    </div>
  );
}

export default App;
