import React, { useState, useEffect } from 'react';

function PatientDashboard({ user }) {
  const [dashboardData, setDashboardData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDashboardData = async () => {
      console.log('Fetching dashboard data for:', user.username);
      try {
        const response = await fetch(`http://localhost:5001/api/patients/dashboard?username=${user.username}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });

        const data = await response.json();
        console.log('Dashboard data:', data);
        if (response.ok) {
          setDashboardData(data);
        } else {
          setError(data.error || 'Failed to fetch dashboard data');
        }
      } catch (error) {
        console.error('Fetch error:', error);
        setError('Network error. Please try again.');
      }
    };

    if (user && user.username) {
      fetchDashboardData();
    } else {
      console.log('User or username is undefined:', user);
    }
  }, [user]);

  if (error) return <p className="text-red-500">{error}</p>;
  if (!dashboardData) return <p>Loading...</p>;

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Patient Dashboard</h2>
      <div className="space-y-4">
        <p><strong>Welcome,</strong> {dashboardData.fullName || dashboardData.username}!</p>
        <p><strong>Role:</strong> {dashboardData.role}</p>
        <p><strong>User ID:</strong> {dashboardData.userId}</p>
        <p><strong>Birth Date:</strong> {new Date(dashboardData.birthDate).toLocaleDateString()}</p>
        <p><strong>Phone:</strong> {dashboardData.phone}</p>
        <p><strong>Address:</strong> {dashboardData.address}</p>
        <p><strong>Chronic Disease:</strong> {dashboardData.chronicDisease}</p>
        <p><strong>Drug Allergy:</strong> {dashboardData.drugAllergy}</p>
        <p><strong>Food Allergy:</strong> {dashboardData.foodAllergy}</p>
        <p><strong>Next Appointment:</strong> {dashboardData.appointmentDate ? new Date(dashboardData.appointmentDate).toLocaleString() : 'None'}</p>
        <p><strong>Status:</strong> {dashboardData.status}</p>
        <p><strong>Staff ID:</strong> {dashboardData.staffId || 'None'}</p>
        <p><strong>Treatment ID:</strong> {dashboardData.treatmentId || 'None'}</p>
      </div>
    </div>
  );
}

export default PatientDashboard;
