import React, { useState, useEffect } from "react";

function DoctorDashboard({ user }) {
  const [appointments, setAppointments] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [treatmentNotes, setTreatmentNotes] = useState("");
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // โหลดนัดหมายของหมอจาก backend
    const fetchAppointments = async () => {
      try {
        const response = await fetch(`/api/doctor/${user.id}/appointments`);
        const data = await response.json();
        setAppointments(data);
      } catch (error) {
        console.error("Error fetching appointments:", error);
      }
    };
    fetchAppointments();
  }, [user.id]);

  const handleViewPatient = (patient) => {
    setSelectedPatient(patient);
    setShowModal(true);
  };

  const handleSaveTreatment = async (appointmentId) => {
    try {
      const res = await fetch(`/api/appointments/${appointmentId}/treatment`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ notes: treatmentNotes }),
      });
      if (res.ok) {
        alert("บันทึกการรักษาเรียบร้อย");
        setTreatmentNotes("");
        setShowModal(false);
      } else {
        alert("เกิดข้อผิดพลาดในการบันทึกการรักษา");
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-blue-200 p-8">
      <h2 className="text-3xl font-bold text-gray-800 text-center mb-6">
        Doctor Dashboard
      </h2>
      <p className="text-center mb-6">Welcome, {user.username}!</p>

      <div className="bg-white shadow-lg rounded-2xl p-6">
        <h3 className="text-xl font-semibold mb-4">รายการนัดหมาย</h3>
        <table className="w-full border-collapse border border-gray-200">
          <thead>
            <tr className="bg-gray-100 text-left">
              <th className="p-2 border">ชื่อผู้ป่วย</th>
              <th className="p-2 border">วันที่นัด</th>
              <th className="p-2 border">สถานะ</th>
              <th className="p-2 border">การจัดการ</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((app) => (
              <tr key={app.id} className="hover:bg-gray-50">
                <td className="p-2 border">{app.patient.fullName}</td>
                <td className="p-2 border">
                  {new Date(app.dateTime).toLocaleString()}
                </td>
                <td className="p-2 border">{app.status}</td>
                <td className="p-2 border space-x-2">
                  <button
                    onClick={() => handleViewPatient(app.patient)}
                    className="px-3 py-1 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                  >
                    ดูข้อมูล
                  </button>
                  <button
                    onClick={() => handleViewPatient(app.patient)}
                    className="px-3 py-1 bg-green-500 text-white rounded-lg hover:bg-green-600"
                  >
                    บันทึกการรักษา
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal แสดงข้อมูลผู้ป่วย */}
      {showModal && selectedPatient && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-2xl p-6 w-1/2 shadow-lg">
            <h3 className="text-lg font-semibold mb-4">ข้อมูลผู้ป่วย</h3>
            <p><b>ชื่อ-นามสกุล:</b> {selectedPatient.fullName}</p>
            <p><b>อายุ:</b> {selectedPatient.age || "-"}</p>
            <p><b>โรคประจำตัว:</b> {selectedPatient.chronicDisease || "-"}</p>
            <p><b>แพ้ยา:</b> {selectedPatient.drugAllergy || "-"}</p>
            <p><b>แพ้อาหาร:</b> {selectedPatient.foodAllergy || "-"}</p>

            <textarea
              value={treatmentNotes}
              onChange={(e) => setTreatmentNotes(e.target.value)}
              placeholder="กรอกรายละเอียดการรักษา..."
              className="w-full border p-2 rounded-lg mt-4"
            />

            <div className="flex justify-end mt-4 space-x-2">
              <button
                onClick={() => setShowModal(false)}
                className="px-4 py-2 bg-gray-400 text-white rounded-lg hover:bg-gray-500"
              >
                ปิด
              </button>
              <button
                onClick={() => handleSaveTreatment(selectedPatient.id)}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
              >
                บันทึกการรักษา
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DoctorDashboard;
