import React, { useState, useEffect } from "react";

function PatientDashboard({ user }) {
  const [dashboard, setDashboard] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const res = await fetch(`http://localhost:5001/api/patients/dashboard?username=${user.username}`);
      const data = await res.json();
      setDashboard(data);
    };
    if (user) fetchData();
  }, [user]);

  const confirmAppointment = () => alert("ยืนยันนัดหมายสำเร็จ!");
  const rescheduleAppointment = () => alert("เลื่อนนัดหมาย");
  const cancelAppointment = () => alert("ยกเลิกนัดหมาย");

  const isConfirmEnabled = () => {
    if (!dashboard?.appointment?.dateTime) return false;
    const appDate = new Date(dashboard.appointment.dateTime);
    const today = new Date();
    const oneDayBefore = new Date(appDate);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appDate;
  };

  if (!dashboard) return <p>Loading...</p>;

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      <div className="bg-gray-100 p-4 rounded-lg shadow mb-4">
        <p><strong>Name:</strong> {user.fullName}</p>
        <p><strong>ID:</strong> {user.userId}</p>
      </div>

      {dashboard.appointment && (
        <div className="bg-gray-100 p-4 rounded-lg shadow">
          <p><strong>Appointment:</strong> {new Date(dashboard.appointment.dateTime).toLocaleDateString()}</p>
          <p><strong>Status:</strong> {dashboard.appointment.status}</p>
          <div className="mt-4 space-x-2">
            <button onClick={confirmAppointment} disabled={!isConfirmEnabled()} className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 ${!isConfirmEnabled() ? "opacity-50 cursor-not-allowed" : ""}`}>ยืนยันนัดหมาย</button>
            <button onClick={rescheduleAppointment} className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600">เลื่อนนัดหมาย</button>
            <button onClick={cancelAppointment} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">ยกเลิกนัด</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default PatientDashboard;
