import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function PatientDashboard() {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const username = new URLSearchParams(location.search).get('username') || 'testuser';

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(username)}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        const data = await response.json();
        console.log('Dashboard data:', data);
        if (response.ok) {
          setPatientData(data);
        } else {
          setError(data.error || 'Failed to fetch patient data');
        }
      } catch (err) {
        console.error('Network error:', err);
        setError('Network error');
      }
    };
    fetchPatientData();
  }, [username]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return 'N/A';
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    return `${years} ปี`;
  };

  const confirmAppointment = () => {
    alert('ยืนยันนัดหมายสำเร็จ!');
  };

  const rescheduleAppointment = () => {
    navigate('/booking');
  };

  const cancelAppointment = () => {
    navigate('/booking');
  };

  const viewReceipt = () => {
    navigate(`/receipt/${patientData.patientId}`);
  };

  const isConfirmEnabled = () => {
    if (!patientData?.appointmentDate) return false;
    const appointment = new Date(patientData.appointmentDate);
    const today = new Date();
    const oneDayBefore = new Date(appointment);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appointment;
  };

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      {patientData ? (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-1 bg-gray-100 p-4 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">Patient Info</h2>
            <p><strong>Name:</strong> {patientData.fullName || 'Not provided'}</p>
            <p><strong>ID:</strong> {patientData.patientId || 'N/A'}</p>
            <p><strong>Age:</strong> {calculateAge(patientData.birthDate)}</p>
          </div>
          <div className="md:col-span-2 bg-gray-100 p-4 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">Appointment</h2>
            <p><strong>Date:</strong> {patientData.appointmentDate ? new Date(patientData.appointmentDate).toLocaleDateString() : 'N/A'}</p>
            <p><strong>Status:</strong> {patientData.status || 'Pending'}</p>
            <div className="mt-4 space-x-2">
              <button
                onClick={confirmAppointment}
                disabled={!isConfirmEnabled()}
                className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 ${!isConfirmEnabled() ? 'opacity-50 cursor-not-allowed' : ''}`}
              >
                ยืนยันนัดหมาย
              </button>
              <button
                onClick={rescheduleAppointment}
                className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
              >
                เลื่อนนัดหมาย
              </button>
              <button
                onClick={cancelAppointment}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >
                ยกเลิกนัด
              </button>
            </div>
          </div>
          <div className="md:col-span-3 bg-gray-100 p-4 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">Past Receipts</h2>
            <button
              onClick={viewReceipt}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              disabled={!patientData.patientId}
            >
              ดูใบเสร็จย้อนหลัง
            </button>
          </div>
        </div>
      ) : (
        <p className="text-center">Loading...</p>
      )}
    </div>
  );
}

export default PatientDashboard;
