import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login({ onLogin }) {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5001/api/patients/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      console.log('Response data from login:', data);

      if (response.ok) {
        const { patient } = data;
        if (patient) {
          // ดึงข้อมูลเพิ่มเติมจาก dashboard
          const dashboardResponse = await fetch(`http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(patient.username)}`, {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
          });
          const dashboardData = await dashboardResponse.json();
          console.log('Dashboard data:', dashboardData);

          if (!dashboardResponse.ok) {
            throw new Error(dashboardData.error || 'Failed to fetch dashboard data');
          }

          const userData = {
            username: dashboardData.username || patient.username || '',
            patientId: dashboardData.patientId || patient.id || null,
            fullName: dashboardData.fullName || patient.fullName || '',
            role: dashboardData.role || patient.role || 'patient',
            birthDate: dashboardData.birthDate || null,
            chronicDisease: dashboardData.chronicDisease || '',
            drugAllergy: dashboardData.drugAllergy || '',
            foodAllergy: dashboardData.foodAllergy || '',
          };
          onLogin(userData);
          console.log('User data sent to App.jsx:', userData);
          const dashboardPath = userData.role === 'doctor' ? '/doctor-dashboard' :
                              userData.role === 'staff' ? '/staff-dashboard' :
                              '/patient-dashboard';
          console.log('Navigating to:', dashboardPath);
          navigate(dashboardPath);
        } else {
          setError('Invalid response from server');
        }
      } else {
        setError(data.error || 'Login failed');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'Network error or server unavailable');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-white to-blue-200 flex items-center justify-center">
      <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition duration-500 hover:scale-105">
        <h2 className="text-3xl font-bold text-gray-800 text-center mb-6">Welcome Back</h2>
        <p className="text-center text-gray-500 mb-8">Please sign in to your account</p>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-2 rounded mb-4 text-center">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200"
              placeholder="Enter your username"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200"
              placeholder="Enter your password"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 focus:ring-4 focus:ring-blue-300 transition duration-200 font-semibold"
          >
            Sign In
          </button>
        </form>

        <p className="text-center text-sm text-gray-500 mt-4">
          Don't have an account? <a href="/register" className="text-blue-600 hover:underline">Sign Up</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
