import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function DoctorDashboard({ user }) {
  const [patients, setPatients] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/patients/appointments?role=doctor', {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        const data = await response.json();
        console.log('Doctor patients data:', data);
        if (response.ok) {
          setPatients(data);
        } else {
          setError(data.error || 'Failed to fetch patients');
        }
      } catch (err) {
        console.error('Network error:', err);
        setError('Network error');
      }
    };
    fetchPatients();
  }, []);

  const startTreatment = (patientId) => {
    navigate(`/treatment/${patientId}`);
  };

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Doctor Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      {patients.length > 0 ? (
        <div className="bg-gray-100 p-4 rounded-lg shadow">
          <h2 className="text-xl font-semibold text-gray-700 mb-2">Patient Appointments Today</h2>
          <table className="w-full border-collapse border border-blue-300">
            <thead>
              <tr className="bg-blue-100">
                <th className="border border-blue-300 p-2">Patient ID</th>
                <th className="border border-blue-300 p-2">Name</th>
                <th className="border border-blue-300 p-2">Appointment Date</th>
                <th className="border border-blue-300 p-2">Status</th>
                <th className="border border-blue-300 p-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {patients.map((patient) => (
                <tr key={patient.patientId} className="hover:bg-blue-50">
                  <td className="border border-blue-300 p-2">{patient.patientId}</td>
                  <td className="border border-blue-300 p-2">{patient.fullName}</td>
                  <td className="border border-blue-300 p-2">{new Date(patient.appointmentDate).toLocaleDateString()}</td>
                  <td className="border border-blue-300 p-2">{patient.status || 'Pending'}</td>
                  <td className="border border-blue-300 p-2">
                    {patient.status === 'Checked In' && (
                      <button
                        onClick={() => startTreatment(patient.patientId)}
                        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                      >
                        ทำการรักษา
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center">No appointments today.</p>
      )}
    </div>
  );
}

export default DoctorDashboard;
