import React from 'react';

function PromotionSection() {
  const promotions = [
    { 
      id: 1, 
      title: 'ฟอกสีฟัน 50% Off', 
      description: 'โปรโมชั่นพิเศษสำหรับการฟอกสีฟัน ครบทุกเคส!', 
      image: 'https://via.placeholder.com/300x200?text=Teeth+Whitening', // URL รูปภาพตัวอย่าง
      duration: 'ถึง 30 ก.ย. 2025' 
    },
    { 
      id: 2, 
      title: 'ตรวจฟันฟรี', 
      description: 'ตรวจสุขภาพช่องปากฟรีสำหรับสมาชิกใหม่', 
      image: 'https://via.placeholder.com/300x200?text=Free+Checkup', // URL รูปภาพตัวอย่าง
      duration: 'ถึง 15 ต.ค. 2025' 
    },
  ];

  return (
    <section className="py-8 bg-white">
      <h2 className="text-3xl font-bold text-gray-800 text-center mb-6">โปรโมชั่นพิเศษ</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-6xl mx-auto px-4">
        {promotions.map((promo) => (
          <div key={promo.id} className="bg-blue-100 p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
            <img src={promo.image} alt={promo.title} className="w-full h-48 object-cover rounded-md mb-4" />
            <h3 className="text-xl font-semibold text-gray-700">{promo.title}</h3>
            <p className="mt-2 text-gray-600">{promo.description}</p>
            <p className="mt-2 text-sm text-gray-500">ระยะเวลา: {promo.duration}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default PromotionSection;
