import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom'; // ไม่ต้อง import Router
import Login from './components/Login';
import Register from './components/Register';
import PatientDashboard from './components/PatientDashboard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  const [user, setUser] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);

  // โหลด user จาก localStorage เมื่อ App เริ่มทำงาน
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (err) {
        console.error('Failed to parse user from localStorage:', err);
        localStorage.removeItem('user');
        setUser(null);
      }
    }
    setLoadingUser(false);
  }, []);

  const handleLogin = (userData) => {
    console.log('Handling login with userData:', userData);
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  if (loadingUser) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mx-auto p-4">
      <Routes>
        <Route
          path="/login"
          element={user ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />}
        />
        <Route
          path="/register"
          element={user ? <Navigate to="/dashboard" /> : <Register />}
        />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute user={user} role="patient">
              <PatientDashboard user={user} onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        {/* redirect หน้าอื่นไป login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </div>
  );
}

export default App;
