import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import PatientDashboard from './components/PatientDashboard';
import PromotionSection from './components/PromotionSection';
import MapSection from './components/MapSection';
import ReviewSection from './components/ReviewSection';

function App() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) setUser(JSON.parse(savedUser));
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="p-4 bg-blue-600 text-white flex justify-between">
        <div>
          <Link to="/" className="mr-4">Home</Link>
          {user?.role === 'patient' && <Link to="/patient-dashboard">Dashboard</Link>}
        </div>
        <div>
          {user ? <button onClick={handleLogout} className="bg-red-500 p-2 rounded">Logout</button> :
            <>
              <Link to="/login" className="bg-green-500 p-2 rounded mr-2">Login</Link>
              <Link to="/register" className="bg-blue-500 p-2 rounded">Sign Up</Link>
            </>}
        </div>
      </header>

      <main className="p-4">
        <Routes>
          <Route path="/" element={<><PromotionSection /><MapSection /><ReviewSection /></>} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/patient-dashboard" element={user?.role==='patient' ? <PatientDashboard user={user} /> : <Navigate to="/login" replace />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
