// PatientDashboard.jsx
import React, { useState, useEffect } from "react";

function PatientDashboard({ user }) {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPatientData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(
            user.username
          )}`,
          { method: "GET", headers: { "Content-Type": "application/json" } }
        );

        const data = await response.json();
        if (response.ok) {
          setPatientData(data);
        } else {
          setError(data.error || "Failed to fetch patient data");
        }
      } catch (err) {
        console.error("Network error:", err);
        setError("Network error");
      }
    };

    fetchPatientData();
  }, [user.username]);

  const calculateAge = (birthDate) => {
    if (!birthDate) return "N/A";
    const birth = new Date(birthDate);
    const today = new Date();
    let years = today.getFullYear() - birth.getFullYear();
    return `${years} ปี`;
  };

  const confirmAppointment = () => alert("ยืนยันนัดหมายสำเร็จ!");
  const rescheduleAppointment = () => alert("เลื่อนนัดหมาย (เลือกวันใหม่)");
  const cancelAppointment = () => alert("ยกเลิกนัดหมายเรียบร้อย");

  const isConfirmEnabled = () => {
    if (!patientData?.appointmentDate) return false;
    const appointment = new Date(patientData.appointmentDate);
    const today = new Date();
    const oneDayBefore = new Date(appointment);
    oneDayBefore.setDate(oneDayBefore.getDate() - 1);
    return today >= oneDayBefore && today < appointment;
  };

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen rounded shadow">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>

      {/* Error */}
      {error && <div className="text-red-500 mb-4">{error}</div>}

      {/* Patient Info */}
      {patientData && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-1 bg-gray-100 p-4 rounded-lg shadow">
            <h2 className="text-xl font-semibold text-gray-700 mb-2">
              ข้อมูลคนไข้
            </h2>
            <p>
              <strong>Name:</strong> {patientData.fullName}
            </p>
            <p>
              <strong>ID:</strong> {patientData.userId}
            </p>
            <p>
              <strong>Age:</strong> {calculateAge(patientData.birthDate)}
            </p>
          </div>

          {/* Appointment */}
          {patientData.appointmentDate && (
            <div className="md:col-span-2 bg-gray-100 p-4 rounded-lg shadow">
              <h2 className="text-xl font-semibold text-gray-700 mb-2">
                นัดหมาย
              </h2>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(patientData.appointmentDate).toLocaleDateString()}
              </p>
              <p>
                <strong>Status:</strong> {patientData.status || "Pending"}
              </p>

              <div className="mt-4 flex space-x-2">
                <button
                  onClick={confirmAppointment}
                  disabled={!isConfirmEnabled()}
                  className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 ${
                    !isConfirmEnabled() ? "opacity-50 cursor-not-allowed" : ""
                  }`}
                >
                  ยืนยันนัดหมาย
                </button>
                <button
                  onClick={rescheduleAppointment}
                  className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
                >
                  เลื่อนนัดหมาย
                </button>
                <button
                  onClick={cancelAppointment}
                  className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                >
                  ยกเลิกนัด
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      {!patientData && !error && <p className="text-center">Loading...</p>}
    </div>
  );
}

export default PatientDashboard;
