import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function PatientDashboard({ user }) {
  const [patientData, setPatientData] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (!user?.username) return;

    const fetchPatientData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/patients/dashboard?username=${encodeURIComponent(user.username)}`);
        const data = await response.json();

        if (response.ok) setPatientData(data);
        else setError(data.error || 'Failed to fetch patient data');
      } catch (err) {
        console.error(err);
        setError('Network error');
      }
    };

    fetchPatientData();
  }, [user]);

  if (!user) return <p>Please login first</p>;

  return (
    <div className="container mx-auto p-6 bg-white text-blue-800 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">Patient Dashboard</h1>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      {patientData ? (
        <div>
          <p><strong>Name:</strong> {patientData.fullName}</p>
          <p><strong>ID:</strong> {patientData.userId}</p>
          <p><strong>Phone:</strong> {patientData.phone}</p>
          <p><strong>Appointment:</strong> {patientData.appointmentDate || 'N/A'}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default PatientDashboard;
